        string blackPixelString = "ff000000";
        public String parsePattern(Bitmap cardBitmap)
        {
            List<int> currentCardPattern = new List<int>();
            for (int x = 0; x < cardBitmap.Width; x++)
            {
                for (int y = 0; y < cardBitmap.Height; y++)
                {
                    var pixel = cardBitmap.GetPixel(x, y);
                    if (pixel.Name.ToString() == blackPixelString)
                    {
                        currentCardPattern.Add(1);
                    }
                    else
                    {
                        currentCardPattern.Add(0);
                    }
                }
            }

            /*string stringPattern = "";
            foreach (int pixelInt in currentCardPattern)
            {
                stringPattern += pixelInt.ToString() + ",";
            }*/

            CardPattern resultCardPattern = knownCardPatterns[0];
            int max = 0;
            foreach (CardPattern knownCardPattern in knownCardPatterns)
            {
                if (cardBitmap.Width != knownCardPattern.width)
                {
                    continue;
                }
                int current = intersectionRate(knownCardPattern.pixels, currentCardPattern);
                if (current > max)
                {
                    max = current;
                    resultCardPattern = knownCardPattern;
                }
            }

            return resultCardPattern.Name;
        }

        private int intersectionRate(List<int> known, List<int> inc)
        {
            int result = 0;

            int size = Math.Min(known.Count, inc.Count);

            for (int i = 0; i < size; i++)
            {
                if (known[i] == inc[i])
                {
                    //сколько совпало черных пикселей
                    if (known[i] == 1)
                    {
                        result += 1;
                    } 
                    //result += 1;
                }
            }

            return result;
        }

    }
